package PracticeExample;

public class areaofsphere {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		double area,radius=5;
		System.out.println("radius of sphere="+radius);
		area=4*3.14*radius*radius;//sphere formula=4*pie*radius*radius
		System.out.println("Area of sphere="+area);
		
		
		double pie=3.14,area1,radius1=14;
		area1=pie*radius1*radius1;
		System.out.println("Area of circle="+area1);
	}

}
