package if_else_practice;

public class upperlower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='m';
		if( ch>=65 && ch<=90)
		{
			System.out.println("Given character is uppercase="+ch);
		}
		else
		{
			System.out.println("Is lower case="+ch);
		}

	}

}
